public class Texture {
	private int id; // Texture ID

	// Constructor which takes in a filename and generates a texture with it
	public Texture(String filename) {
		this.id = loadTexture(filename);
	}

	// Returns the value of the `id` field
	public int getID() {
		return id;
	}

	// Loads a 2D image from a file and generates a texture for it, using the GL_TEXTURE_2D format. It sets up the texture parameters and uploads the image data to the GPU.
	private static int loadTexture(String filename) {
		try {
			BufferedImage image = ImageIO.read(new File(filename));
			int[] pixels = image.getRGB(0, 0, image.getWidth(), image.getHeight(), null, 0, image.getWidth());

			ByteBuffer buffer = BufferUtils.createByteBuffer(image.getHeight() * image.getWidth() * 4);
			boolean hasAlpha = image.getColorModel().hasAlpha();

			for (int y = 0; y < image.getHeight(); y++) {
				for (int x = 0; x < image.getWidth(); x++) {
					int pixel = pixels[y * image.getWidth() + x];

					buffer.put((byte) ((pixel >> 16) & 0xFF));
					buffer.put((byte) ((pixel >> 8) & 0xFF));
					buffer.put((byte) ((pixel) & 0xFF));
					if (hasAlpha) {
						buffer.put((byte) ((pixel >> 24) & 0xFF));
					} else {
						buffer.put((byte) (0xFF));
					}
				}
			}

			buffer.flip();

			int id = GL11.glGenTextures();
			GL11.glBindTexture(GL_TEXTURE_2D, id);

			GL11.glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL12.GL_REPEAT);
			GL11.glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL12.GL_REPEAT);

			GL11.glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL11.GL_LINEAR);
			GL11.glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL11.GL_LINEAR);

//			GL30.glGenerateMipmap(GL_TEXTURE_2D);
//			GL11.glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL11.GL_LINEAR_MIPMAP_LINEAR);

			GL11.glTexImage2D(GL_TEXTURE_2D, 0, GL11.GL_RGBA8, image.getWidth(), image.getHeight(), 0, GL11.GL_RGBA, GL11.GL_UNSIGNED_BYTE, buffer);

			return id;
		} catch (Exception e) {
			e.printStackTrace();
			System.exit(1);
		}

		return 0;
	}
}

