public class PerspectiveCamera extends CameraStruct3D {
    public static void main(String[] args) {
        // create a perspective camera with 90 degrees field of view, 16:9 aspect ratio, 0.1 unit near plane distance, and 500 units far plane distance
        PerspectiveCamera cam = new PerspectiveCamera(Math.toRadians(90), 16f/9f, 0.1f, 500f);

        // use the camera's perspective matrix to transform a point in world space into screen space
        Matrix4f perspMat = cam.getAsMatrix4();
        Vector3f pointInWorldSpace = new Vector3f(10f, 10f, 10f);
        System.out.println("pointInWorldSpace: " + pointInWorldSpace);
        Vector4f transformedPoint = perspMat.transform(new Vector4f(pointInWorldSpace));
        System.out.println("transformedPoint: " + transformedPoint);
    }
}

