public class Game {
    private Shader s;
    private Camera c;

    public void run() {
        // create shader, camera, and world
        this.s = new Shader();
        this.c = new Camera();
        World w = new World();

        while (true) {
            // render the scene
            s.use();
            c.setView(new Vec3f());
            c.setProjection(Matrix4f.IDENTITY);
            w.render(s, c);
        }
    }
}

